import { useState } from "react";

function App() {
  const [count, setCount] = useState(0);
  return (
    <div className="container">
      <h1>Count :{count}</h1>
      <button
        onClick={() => {
          setCount(count + 1);
        }}
      >
        Increase
      </button>
      <button
        onClick={() => {
          if (count === 0) {
            return;
          }
          setCount(count - 1);
        }}
      >
        Decrease
      </button>
    </div>
  );
}

export default App;
