import React, { useContext } from "react";
import AppContext from "../provider/Context";
function ListStudent() {
  const {students, stuDetails} = useContext(AppContext);

  const getDetail = (stuId) => {
    // trả về object student detail
    return stuDetails.find((stu) => stu.studentId === stuId);
  }

  return (
    <div>
      <h3>List of Students</h3>
      <table className="table table-hover">
        <thead>
          <tr>
            <th>StudentId</th>
            <th>Name</th>
            <th>Age</th>
            <th>Street</th>
            <th>City</th>
            <th>RegularStudent</th>
            <th>View grades</th>
          </tr>
        </thead>
        <tbody>
          {students.map((stu, index) => (
            <tr key={index}>
              <td>{stu.studentId}</td>
              <td>{stu.name}</td>
              <td>{stu.age}</td>
              <td>{getDetail(stu.studentId)?.address?.street}</td>
              <td>{getDetail(stu.studentId)?.address?.street}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ListStudent;
