import { Route, Routes } from "react-router-dom";
import { useContext } from "react";
import AppContext from "./provider/Context";

function App() {
  const { students } = useContext(AppContext);
  console.log(todos);

  return (
    <div className="container">
      {students.map((todo, index) => (
        <div key={index}>
          <p>{todo.id}</p>
          <p>{todo.title}</p>
          {/*The inner curly braces (style) are used to define an object in JavaScript.*/}
          <p style={{color: todo.completed ? 'green' : 'red'}} >{todo.completed ? 'ok' : 'not ok'}</p> 
        </div>
      ))}
    </div>
  );
}

export default App;
